mapper.update:
update tag
set name = #{name,jdbcType=VARCHAR},
  category_id = #{categoryId,jdbcType=tinyint},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=smallint}

mapper.select:
select id, name, category_id, create_time
from tag
where id = #{id,jdbcType=smallint}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into tag (name, category_id, create_time
  )
values (#{name,jdbcType=VARCHAR}, #{categoryId,jdbcType=TINYINT}, #{createTime,jdbcType=TIMESTAMP}
  )

